#[proc_macro]
pub fn process_string(input: TokenStream) -> TokenStream {
    let input_str = parse_macro_input!(input as LitStr).value();

    let mut result_vec = Vec::new();

    for ch in input_str.chars() {
        if true {
            let rand_str = random_string(&ch).unwrap_or_else(|| generate_random_string(&ch));

            if let Some(position) = rand_str.find(ch) {
                result_vec.push(quote! { #rand_str.chars().nth(#position).unwrap() });
            } else if let Some(position) = rand_str.find(ch.to_ascii_lowercase()) {
                result_vec.push(quote! { #rand_str.chars().nth(#position).unwrap().to_ascii_uppercase() });
            }
        } else {
            result_vec.push(quote! { #ch });
        }
    }
    let kuohao = "{}".repeat(result_vec.len());
    let result = quote! {
        format!(#kuohao,#(#result_vec,)*)
    };

    TokenStream::from(result)
}



fn random_string(ch: &char) -> Option<String> {
    let rand_str = STRINGS_MAP.get(&ch.to_ascii_lowercase());
    return if let Some(rand_str) = rand_str {
        Some(rand_str.choose(&mut thread_rng()).unwrap().to_string())
    } else { None }
}


fn generate_random_string(ch: &char)->String{
    let mut a=String::new();
    let words=vec!["the","might","higher","laugh","highly","her","his","highest","get","earliest","access","search","low","lower","lowest","lowly","little","lost","low","opportunity","lose","nano","hot"];
    a.push_str(words.choose(&mut thread_rng()).unwrap());
    a.push(ch.clone());
    a.push_str(words.choose(&mut thread_rng()).unwrap());
    a
}